public class SampleActiveMQApplication { 
    @Bean 
        public Queue queue() { 
            return new ActiveMQQueue("sample.queue"); 
        } 
    public static void main(String[] args) { 
        SpringApplication.run(SampleActiveMQApplication.class, args); 
    } 
}
