private static void consumer(final DelayQueue<Order> delayQueue) {
    new Thread(new Runnable() {
        @Override
        public void run() {
            while (true) {
                Order element = null;
                try {
                    element = delayQueue.take();

                    if (element.getState().intValue() == 0) {
                        // 
                        element.setState(1);

                        System.out.println("" + element.getId() + "");
                    }
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
            }
        }
    }).start();
}
