public synchronized boolean getScanStatus() {
    try {
        if (!isScan()) { //
            this.wait();
        }
        if (isScan()) {
            return true;
        }
    } catch (InterruptedException e) {
        // TODO Auto-generated catch block
        e.printStackTrace();
    }
    return false;
}

public synchronized void notifyPool() {
    try {
        this.notifyAll();
    } catch (Exception e) {
        e.printStackTrace();
    }
}
