# 

```
<!DOCTYPE html>
<html>
<head>
<title>websocket</title>
<script src="//cdn.bootcss.com/angular.js/1.5.6/angular.min.js"></script>
<script src="https://cdn.bootcss.com/sockjs-client/1.1.4/sockjs.min.js"></script>
<script src="https://cdn.bootcss.com/stomp.js/2.3.3/stomp.min.js"></script>
<script type="text/javascript">

	var stompClient = null;

	var app = angular.module('app', []);
	app.controller('MainController', function($rootScope, $scope, $http) {

		$scope.data = {
			//
			connected : false,
			//
			message : '',
			rows : []
		};

		//
		$scope.connect = function() {
			var socket = new SockJS('/my-websocket');
			stompClient = Stomp.over(socket);
			stompClient.connect({}, function(frame) {
				// 
				stompClient.subscribe('/topic/send', function(msg) {
					$scope.data.rows.push(JSON.parse(msg.body));
					$scope.data.connected = true;
					$scope.$apply();
				});
				// 
				stompClient.subscribe('/topic/callback', function(r) {
					$scope.data.time = '' + r.body;
					$scope.data.connected = true;
					$scope.$apply();
				});

				$scope.data.connected = true;
				$scope.$apply();
			});
		};

		$scope.disconnect = function() {
			if (stompClient != null) {
				stompClient.disconnect();
			}
			$scope.data.connected = false;
		}

		$scope.send = function() {
			stompClient.send("/app/send", {}, JSON.stringify({
				'message' : $scope.data.message
			}));
		}
	});
</script>
</head>
<body ng-app="app" ng-controller="MainController">
	<label>WebSocket:</label>
	<button type="button" ng-disabled="data.connected" ng-click="connect()"></button>
	<button type="button" ng-click="disconnect()"
		ng-disabled="!data.connected"></button>
	<br />
	<br />
	<div ng-show="data.connected">
		<label>{{data.time}}</label> <br /> <br /> <input type="text"
			ng-model="data.message" placeholder="..." />
		<button ng-click="send()" type="button"></button>
		<br /> <br />  <br />
		<table>
			<thead>
				<tr>
					<th></th>
					<th></th>
				</tr>
			</thead>
			<tbody>
				<tr ng-repeat="row in data.rows">
					<td>{{row.message}}</td>
					<td>{{row.date}}</td>
				</tr>
			</tbody>
		</table>
	</div>
</body>
</html>
```
